package com.arkc;

public class A007_NumberSystemPrints {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Decimal numbers - No leading digits");
		System.out.println(12345);

		System.out.println("Binary numbers - 0B as leading digits");
//		System.out.println(0B1111);

		System.out.println("Octal numbers - 0 as leading digits");
		System.out.println(07777);

		System.out.println("Hexadecimal numbers - 0X as leading digits");
		System.out.println(0XFFFF);

	}

}
