package com.arkc;

public class A008_NumericDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Numeric datatypes in increasing order of their range\n");
		
		System.out.println("byte   : 256 numbers");
		System.out.println("short  : 65536 numbers");
		System.out.println("int    : 4294967296 numbers");
		System.out.println("long   : ~ 18446744073709600000 numbers");
		System.out.println("float");
		System.out.println("double");

	}

}
